                    <h5 class="modal-title" id="detailModalLabel">Modal title</h5>
                    <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div class="modal-body">
                    <img class="mx-auto mb-3" id="image-detail" width="300" height="auto"></img>
                    <div id="message"></div>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                </div>
            </div>
        </div>
    </div>
    <script src="/gallery/assets/scripts/vanilla.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.0.0-beta2/dist/js/bootstrap.bundle.min.js" integrity="sha384-b5kHyXgcpbZJO/tY9Ul7kGkf1S0CWuKcCD38l8YkeH8z8QjE0GmW1gYU5S9FOnJ0" crossorigin="anonymous"></script>
    <script src="https://kit.fontawesome.com/158176bbc5.js" crossorigin="anonymous"></script>
    <script>
        function delegate(el, evt, sel, handler) {
            el.addEventListener(evt, function(event) {
                var t = event.target;
                while (t && t !== this) {
                    if (t.matches(sel)) {
                        handler.call(t, event);
                    }
                    t = t.parentNode;
                }
            });
        }
        delegate(document, "click", ".cont", function(event) {
            var detailModal = new bootstrap.Modal(document.getElementById('detailModal'), {
                keyboard: false
            });
            document.getElementById("message").innerText = this.dataset.message;
            document.getElementById("detailModalLabel").innerText = this.dataset.title;
            document.getElementById("image-detail").src = this.querySelector('img').src;
            detailModal.show();
        });
    </script>
</body>

</html>